mapper.insert:
INSERT INTO
      t_board_fav
      (
      iboard
    , iuser
       )
VALUES (
      #{iboard}
    , #{iuser});

mapper.select:
SELECT count(iboard)
 FROM  t_board_fav
 WHERE iboard = #{iboard}
 AND   iuser  = #{iuser};

mapper.delete:
DELETE
FROM  t_board_fav
WHERE iboard = #{iboard}
AND   iuser  = #{iuser};
